(5695, '5687', NULL, NULL, NULL, 'Image', 'Immigration', 'Education', NULL, NULL, 'Ireland;Switzerland', 'Disc', 'At least one of my parents migrated here', NULL, 'My mum Margaret was born in Dublin, Republic of Ireland. She went to a boarding school in Waldingham and she returned to Ireland to study at university. She went to Switzerland where she met my pops (dad).', 'Dublin;Family;Ireland;Switzerland', '', NULL, NULL, 'Anandu ', NULL, 0, 0),
(5696, '5688', '', NULL, '', 'Image', 'Immigration', 'Love', 'No details', 'No details', 'Bangladesh', 'Disc', 'At least one of my parents migrated here', NULL, 'My dad came to the UK from Bangladesh after marrying my British mum', 'Bangladesh;Family;UK', '', '', NULL, NULL, '', 0, 0),
