(7105, '7097', NULL, NULL, '2020s', 'Image', 'Immigration', NULL, NULL, NULL, 'India', 'Disc', 'I migrated here', NULL, 'I moved to London from India in 2020.', 'Asia;India', '', NULL, NULL, 'Kate', NULL, 0, 0),
(7106, '7098', 'Naha Kahadia', NULL, '2020s', 'Image', 'Immigration', 'Education', NULL, NULL, NULL, 'Disc', 'I migrated here', NULL, 'I moved to London in 2021 to attend university. Naha Kahadia.', 'Education', '', NULL, NULL, 'Kate', NULL, 0, 0),
(7107, '7099', NULL, NULL, NULL, 'Image', 'Immigration', 'Child Migration', NULL, NULL, 'Italy', 'Disc', 'I migrated here', NULL, 'I migrated from Italy to the UK when I was a few months old then from central London to Bromley.', 'EU;Europe;Italy', '', NULL, NULL, 'Kate', NULL, 0, 0),
(7108, '7100', NULL, NULL, NULL, 'Image', 'Immigration', NULL, NULL, NULL, NULL, 'Disc', 'What would you like to see in our Migration Museum', NULL, '(1) References to the history of early immigration, Anglo-Saxons, Vikings, etc. (2) Clarification at the entrance that the museum covers a very wide range of migrant experience - I was expecting a Windrush focus.', NULL, '', NULL, NULL, 'Kate', NULL, 0, 0),
(7109, '7101', 'Naha Kahadia', NULL, NULL, 'Image', 'Immigration', NULL, NULL, NULL, NULL, 'Disc', 'What would you like to see in our Migration Museum', NULL, 'Subtitles on videos. Soft prompts given for open-ended questions. Naha Kahadia.', NULL, '', NULL, NULL, 'Kate', NULL, 0, 0),
(7110, '7102', NULL, NULL, NULL, 'Image', 'Immigration', 'Refugee/Asylum', NULL, NULL, 'Spain', 'Disc', 'I migrated here', NULL, 'I migrated [from] Spain to London because we were forced to.', 'EU;Europe;Spain', '', NULL, NULL, 'Kate', NULL, 0, 0),
(7111, '7103', NULL, NULL, NULL, 'Image', 'Immigration', NULL, NULL, NULL, 'New Zealand', 'Disc', 'At least one of my parents migrated here', NULL, 'My Dad moved to England from New Zealand when he was 19.', 'Australasia;Family;New Zealand;Third person', '', NULL, NULL, 'Kate', NULL, 0, 0),
